import React from "react";
class TaskForm extends React.Component {
    constructor(props){
        super(props);
        this.state={
            name: "",
            status:false
        }
    }
    componentWillMount(){
        if(this.props.taskEditing){
            this.setState({
                id:this.props.taskEditing.id,
                name:this.props.taskEditing.name,
                status:this.props.taskEditing.status
            });
        }
    }
    onClear=()=>{
        this.setState({
            name:'',
            status:false
        });
    }
    onCloseForm=()=>{
        this.props.onCloseForm();
    };
    onChange=(event)=>{
        var target=event.target;
        var name=target.name;
        var value=target.value;
        if(name==="status"){
            value=target.value==="true"?true:false;
        }
        this.setState({
            [name]:value
        });
    };
    onSubmit=(event)=>{
        event.preventDefault();
        this.props.onSubmit(this.state);
        this.onClear();
        this.onCloseForm();
    };
    render() {
        return (
       
                <div className="panel panel-warning">
                    <div className="panel-heading">
                        <h3 className="panel-title">Thêm Công Việc
                        <span className="fa fa-times-circle text-right" onClick={this.onCloseForm}></span>
                        </h3>
                        
                    </div>
                    <div className="panel-body">
                        <form onSubmit={this.onSubmit}>
                            <div className="form-group">
                                <label>Tên :</label>
                                <input type="text" className="form-control"
                                name='name' value={this.state.name} onChange={this.onChange} />
                            </div>
                            <label>Trạng Thái :</label>
                            <select className="form-control" name='status' value={this.state.status} onChange={this.onChange} >
                                <option value={true}>Kích Hoạt</option>
                                <option value={false}>Ẩn</option>
                            </select>
                            <br />
                            <div className="text-center">
                                <button type="submit" className="btn btn-warning"><span className="fa fa-plus mr-5"></span>Thêm</button>&nbsp;
                                <button type="button" className="btn btn-danger" onClick={this.onClear}>Hủy Bỏ</button>
                            </div>
                        </form>
                    </div>
                </div>
        );
    }
}
export default TaskForm;
