import React from "react";
import Header from './Components/Header';
import Products from './Components/Products';
import Footer from "./Components/Footer";
import Banner from "./Components/Banner";
import BannerLeft from "./Components/BannerLeft";
import BannerRight from "./Components/BannerRight";
import Pagination from "./Components/Pagination";
import LeftBottom from "./Components/LeftBottom";
import MiddleBottom from "./Components/MiddleBottom";
import RightBottom from "./Components/RightBottom";
import ScrollTop from "./Components/ScrollTop";

class App extends React.Component {
  constructor(props){
    super(props);
    this.state={
      username:"",
    };
  }
  onHandleChange(event){
    this.setState({
      username:event.target.value
    });
  }
  onHandleSubmit(event){

  }
  render(){
    var products=[
      {
        id: 1,
        ISBN: "ABCXYZ",
        category: "action",
        name: "Kengan Omega",
        author: "Nogul",
        dob: Date,
        des:"Chapter 123",
        view: "5555555",
        picture:"chuong1.jpg",
      },
      {
        id: 2,
        ISBN: "ABCXY1",
        category: "action",
        name: "Kengan Omega",
        author: "Nogul",
        dob: Date,
        des: "Chapter 124",
        view: "123123",
        picture:"chuong1.jpg",
      },
      {
        id: 3,
        ISBN: "ABCXYZ",
        category: "action",
        name: "Kengan Omega",
        author: "Nogul",
        dob: Date,
        des:"Chapter 125",
        view: "1000212",
        picture:"chuong1.jpg",
      },
      {
        id: 4,
        ISBN: "ABCXYZ",
        category: "action",
        name: "Kengan Omega",
        author: "Nogul",
        dob: Date,
        des:"Chapter 126",
        view: "123123",
        picture:"chuong1.jpg",
      },
      {
        id: 5,
        ISBN: "ABCXYZ",
        category: "action",
        name: "Kengan Omega",
        author: "Nogul",
        dob: Date,
        des:"Chapter 127",
        view: "123127",
        picture:"chuong1.jpg",
      },
      {
        id: 6,
        ISBN: "ABCXYZ",
        category: "action",
        name: "Kengan Omega",
        author: "Nogul",
        dob: Date,
        des:"Chapter 128",
        view: "123128",
        picture:"chuong1.jpg",
      }


    ]
    let elements= products.map((product,index)=>{
      return <Products key={product.id} name={product.name} des={product.des} image={product.picture}/>
    });
    return (
      <div>

        <Header />
        <br/> <br/> <br/>
        <div className="banner">    
          <div className="col-xs-3 col-sm-3 col-md-3 col-lg-3">
          <BannerLeft/>
          </div>       
          <div className="col-xs-8 col-sm-8 col-md-8 col-lg-8">
          <Banner/>
          </div>
          <div className="col-xs-3 col-sm-3 col-md-3 col-lg-3">
          <BannerRight/>
          </div>       
        </div> 
        <h1>TRUYỆN ĐỀ CỬ </h1>
        <div className="row">
            <div className="col-xs- col-sm- col-md- col-lg-">
              {elements}
            </div>
        </div>

        <div className="container">         
            <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">   
              <div className="panel panel-primary">
                  <div className="panel-heading">
                    <h3 className="panel-title">Form</h3>
                  </div>
                  <div className="panel-body">
                   <form onSubmit={this.onHandleSubmit} >
                     <legend>Form title</legend>
                     <div className="form-group">
                       <label >label</label>
                       <input type="text" className="form-control" placeholder="Input field"
                       name="txtName" onChange={this.onHandleChange}/>
                     </div>    
                     <button type="submit" className="btn btn-primary">Submit</button>
                     <button type="reset" className="btn btn-primary">Reset</button>
                   </form>
                  </div>

            </div>

          </div>

        </div>

        <ScrollTop/>
        <div className="top_bottom">
          <Pagination/>
        </div>
        <div className="bottom">
          <div className="row"><LeftBottom/>
          <MiddleBottom/>
          <RightBottom/></div>
          <div className="row"><Footer/></div>

        </div>

      </div>

    );

  }

}

export default App;
