import React from "react";
 
class App extends React.Component {
  constructor(props) {
    super(props);
    //Chỉ định một state   
  }
  state = { index: 1 };
  increment =() =>{
    this.setState({
          index: this.state.index + 2
    });
}
 
  render() {
    
    return (
        
      <div>
        <p>Giá trị {this.state.index}</p>
        <button
        //   onClick={() => {
        //     this.setState({
        //       index: this.state.index + 1
        //     })
        //   }}
        onClick={this.increment}
        >
          Tăng
        </button>
        <button
          onClick={() => {
            this.setState({
              index: this.state.index -1
            })
          }}
        >
          Giảm
        </button>
      </div>
    );
    
  }
}

export default App;
