import React from "react";
import Header from './Components/Header';
import Products from './Components/Products';
import Footer from "./Components/Footer";
import Banner from "./Components/Banner";
import BannerLeft from "./Components/BannerLeft";
import BannerRight from "./Components/BannerRight";
import Pagination from "./Components/Pagination";
import LeftBottom from "./Components/LeftBottom";
import MiddleBottom from "./Components/MiddleBottom";
import RightBottom from "./Components/RightBottom";
import ScrollTop from "./Components/ScrollTop";
import TaskForm from "./Components/TaskForm";
import Control from "./Components/Control";
import TaskList from "./Components/TaskList";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      txtUsername: "",
      txtPassword: "",
      txtDes: "",
      sltCategory: 1,
      rdYear: "2020",
      checkStatus: false,
      task: []
    };
    this.onHandleChange = this.onHandleChange.bind(this);
    this.onHandleSubmit = this.onHandleSubmit.bind(this);
  }
  onGenerateData =()=>{
    var tasks=[
      {
        id: this.generateID(),
        name: 'learn Programming',
        status: true
      },
       {
        id: this.generateID(),
        name: 'learn Java',
        status: true
      },
       {
        id: this.generateID(),
        name: 'learn JavaWeb',
        status: true
      }
    ];
    console.log(tasks);
  }
  s4(){
    return Math.floor(1*Math.random()* 0x10000).toString(16).substring(1);
  }
  generateID(){
    return this.s4() +this.s4()+'-'+this.s4()+'-'+this.s4()+'-'+this.s4()+''+this.s4()+this.s4()+this.s4();
  }
  onHandleChange(event) {
    var target = event.target;
    var value = target.type === "checkbox" ? target.checked : target.value;
    var name = target.name;

    this.setState({
      [name]: value
    });
  }
  onHandleSubmit(event) {
    event.preventDefault();
    console.log(this.state);
  }
  render() {
    var products = [
      {
        id: 1,
        ISBN: "ABCXYZ",
        category: "action",
        name: "Kengan Omega",
        author: "Nogul",
        dob: Date,
        des: "Chapter 123",
        view: "5555555",
        picture: "chuong1.jpg",
      },
      {
        id: 2,
        ISBN: "ABCXY1",
        category: "action",
        name: "Kengan Omega",
        author: "Nogul",
        dob: Date,
        des: "Chapter 124",
        view: "123123",
        picture: "chuong1.jpg",
      },
      {
        id: 3,
        ISBN: "ABCXYZ",
        category: "action",
        name: "Kengan Omega",
        author: "Nogul",
        dob: Date,
        des: "Chapter 125",
        view: "1000212",
        picture: "chuong1.jpg",
      },
      {
        id: 4,
        ISBN: "ABCXYZ",
        category: "action",
        name: "Kengan Omega",
        author: "Nogul",
        dob: Date,
        des: "Chapter 126",
        view: "123123",
        picture: "chuong1.jpg",
      },
      {
        id: 5,
        ISBN: "ABCXYZ",
        category: "action",
        name: "Kengan Omega",
        author: "Nogul",
        dob: Date,
        des: "Chapter 127",
        view: "123127",
        picture: "chuong1.jpg",
      },
      {
        id: 6,
        ISBN: "ABCXYZ",
        category: "action",
        name: "Kengan Omega",
        author: "Nogul",
        dob: Date,
        des: "Chapter 128",
        view: "123128",
        picture: "chuong1.jpg",
      }


    ]
    let elements = products.map((product, index) => {
      return <Products key={product.id} name={product.name} des={product.des} image={product.picture} />
    });
    return (
      <div>

        <Header />
        <br /> <br /> <br />
        <div className="banner">
          <div className="col-xs-3 col-sm-3 col-md-3 col-lg-3">
            <BannerLeft />
          </div>
          <div className="col-xs-8 col-sm-8 col-md-8 col-lg-8">
            <Banner />
          </div>
          <div className="col-xs-3 col-sm-3 col-md-3 col-lg-3">
            <BannerRight />
          </div>
        </div>
        <h1>TRUYỆN ĐỀ CỬ </h1>
        <div className="row">
          <div className="col-xs- col-sm- col-md- col-lg-">
            {elements}
          </div>
        </div>

        <div className="container">
          <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
            <div className="panel panel-primary">
              <div className="panel-heading">
                <h3 className="panel-title">Form</h3>
              </div>
              <div className="panel-body">
                <form onSubmit={this.onHandleSubmit} >
                  <legend>Form title</legend>
                  <div className="form-group">
                    <label >Username</label>
                    <input type="text" className="form-control" placeholder="Input field"
                      name="txtUsername" onChange={this.onHandleChange} />


                  </div>
                  <div className="form-group">
                    <label >Author</label>
                    <input type="text" className="form-control" placeholder="Input field"
                      name="txtPassword" onChange={this.onHandleChange} />
                  </div> <div className="form-group">
                    <label >Description</label>

                    <textarea name="txtDes" className="form-control" rows="3" onChange={this.onHandleChange}></textarea>
                  </div>
                  <label >Category</label>

                  <select name="sltCategory" className="form-control" value={this.state.sltCategory} onChange={this.onHandleChange}>
                    <option value={0}>Action</option>
                    <option value={1}>Drama</option>
                    <option value={2}>Adult</option>
                  </select>

                  <div className="radio">
                    <label>
                      <input type="radio" name="rdYear" value="2020" onChange={this.onHandleChange} checked={this.state.rdYear === "2020"} />
                      2020
                    </label>
                    <label>
                      <input type="radio" name="rdYear" value="2021" onChange={this.onHandleChange} checked={this.state.rdYear === "2021"} />
                      2021
                    </label>
                  </div>

                  <div className="checkbox">
                    <label>
                      <input type="checkbox" value={true} onChange={this.onHandleChange}
                        name="checkStatus" checked={this.state.checkStatus === true} />
                      Accept
                    </label>
                  </div>

                  <br />

                  <button type="submit" className="btn btn-primary">Submit</button>
                  <button type="reset" className="btn btn-primary">Reset</button>
                </form>
              </div>

            </div>

          </div>

        </div>
        <div className="container">
          <div className="text-center">
            <h1>Quản Lý Công Việc</h1>
            <hr />
          </div>
        </div>

        <div className="container-fluid task">
          <div className="row">
            <TaskForm />
            <div className="col-xs-8 col-sm-8 col-md-8 col-lg-8">
              <button type="button" className="btn btn-primary">
                <span className="fa fa-plus mr-5"></span>Thêm Công Việc
              </button>
              <button type="button" className="btn btn-danger" onClick={this.onGenerateData}>
                <span className="fa fa-plus mr-5"></span>Generate
              </button>
              <Control />
              <TaskList />
            </div>
          </div>
        </div>
        <ScrollTop />
        <div className="top_bottom">
          <Pagination />
        </div>
        <div className="bottom">
          <div className="row"><LeftBottom />
            <MiddleBottom />
            <RightBottom /></div>
          <div className="row"><Footer /></div>

        </div>

      </div>

    );

  }

}

export default App;
